import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { MenuList } from "../data/cakes";
import "../styles/CakeDetails.css"; // Import CSS file for styling
import Layout from "../components/Layout/Layout";
const [isZoomed, setIsZoomed] = useState(false);
const [transformStyle, setTransformStyle] = useState("scale(1)");

const CakeDetails = () => {
  const { cakeId } = useParams();
  const [isZoomed, setIsZoomed] = useState(false); // State to track image zoom
  const cake = MenuList.find((cake) => cake.id === parseInt(cakeId));

  if (!cake) {
    return <div>Cake not found</div>;
  }

  const toggleZoom = () => {
    setIsZoomed(!isZoomed);
  };

  return (
    <Layout>
    <div className="cake-details-container">
      <div className="cake-content">
        <div className="cake-image-container">
          <div className="image-wrapper">
            <img
              src={cake.image}
              alt={cake.name}
              className="cake-image"
              style={{ transform: transformStyle }}
              onMouseEnter={toggleZoom}
              onMouseLeave={toggleZoom}
            />
          </div>
        </div>
        <div className="small-image-container">
          <img src={cake.image} alt={cake.name} className="small-image" />
        </div>
      </div>
      <div className="cake-details">
        <h2>{cake.name}</h2>
        <p>{cake.description}</p>
        <p>Price: {cake.price}</p>
      </div>
    </div>
  </Layout>
  );
};

const toggleZoom = (event) => {
  const { offsetX, offsetY, target } = event.nativeEvent;
  const { offsetWidth, offsetHeight } = target;

  const x = offsetX / offsetWidth;
  const y = offsetY / offsetHeight;

  const zoomLevel = 1.2; // Adjust the zoom level as needed
  const scale = isZoomed ? 1 : zoomLevel;

  const translateX = -((x * scale - x) / scale) * 100 + "%";
  const translateY = -((y * scale - y) / scale) * 100 + "%";

  const transform = `scale(${scale}) translate(${translateX}, ${translateY})`;

  setIsZoomed(!isZoomed);
  setTransformStyle(transform);
};


export default CakeDetails;
