import React from "react";
import Layout from "./../components/Layout/Layout";
import { Link } from "react-router-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Banner1 from "../images/banner.jpeg";
import Banner2 from "../images/banner.jpeg";
import Banner3 from "../images/banner.jpeg";
import "../styles/HomeStyles.css";

const Home = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  const banners = [
    { image: Banner1, text: "Cakes", description: "Fresh ", link: "/menu" },
    { image: Banner2, text: "Special Offer", description: "Limited Time Offer", link: "/special-offer" },
    { image: Banner3, text: "Free Delivery", description: "On Orders Above $50", link: "/delivery" },
  ];

  const renderBanners = () => {
    return banners.map((banner, index) => (
      <div key={index}>
        <div className="home" style={{ backgroundImage: `url(${banner.image})` }}>
          <div className="headerContainer">
            <h1>{banner.text}</h1>
            <p>{banner.description}</p>
            <Link to={banner.link}>
              <button>ORDER NOW</button>
            </Link>
          </div>
        </div>
      </div>
    ));
  };

  return (
    <Layout>
      <Slider {...settings}>{renderBanners()}</Slider>
    </Layout>
  );
};

export default Home;
