import React from "react";
import { MenuList } from "../data/data";
import { Link } from "react-router-dom";
import {
  Box,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";

const Menu = () => {
  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Our Products</h1>
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "center",
        }}
      >
        {MenuList.map((menu, index) => (
          <Card
            key={index}
            sx={{ maxWidth: 300, display: "flex", m: 2 }}
          >
            <CardActionArea>
              {menu.image && (
                <CardMedia
                  sx={{ minHeight: 200 }}
                  component="img"
                  src={menu.image}
                  alt={menu.name}
                />
              )}
              <CardContent>
                <Typography variant="h5" gutterBottom component="div">
                  {menu.name}
                </Typography>
                <Typography variant="body2">{menu.description}</Typography>
                {menu.name === "Cakes" ? (
                  <Link to="/cakes">
                    <Button variant="contained" color="primary">
                      Check Now
                    </Button>
                  </Link>
                ) :
                
                
                (
                  <Link to={menu.link}>
                    <Button variant="contained" color="primary">
                      Check Now
                    </Button>
                  </Link>
                )}
              </CardContent>
            </CardActionArea>
          </Card>
        ))}
      </Box>
    </div>
  );
};

export default Menu;
