import React from "react";
import { useParams } from "react-router-dom";
import { FoodList } from "../data/foods"; // Assuming FoodList is defined similarly to MenuList
import "../styles/FoodDetails.css"; // Import CSS file for styling
import Layout from "../components/Layout/Layout";

const FoodDetails = () => {
  const { foodId } = useParams();
  const food = FoodList.find((food) => food.id === parseInt(foodId));

  if (!food) {
    return <div>Food not found</div>;
  }

  const handleOrderButtonClick = () => {
    // Replace 'your-company-number' with your company's WhatsApp number
    const whatsappNumber = '9544486876';
    // Get the URL of the selected product image
    const imageUrl = food.image;
    // Construct the message with product details, price, and photo
    const message = `Hi, I would like to order the ${food.name}. Here are the details: ${food.description}. Price: ${food.price}.`;
    // Construct the WhatsApp URL
    const whatsappUrl = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)} ${imageUrl}`;
    // Open WhatsApp chat window
    window.open(whatsappUrl, '_blank');
  };

  return (
    <Layout>
      <div className="food-details-container">
        <div className="food-content">
          <div className="food-image-container">
            <img
              src={food.image}
              alt={food.name}
              className="normal-image"
            />
          </div>
        </div>
        <div className="food-details">
          <h2>{food.name}</h2>
          <p>{food.description}</p>
          <p>Price: {food.price}</p>
          <button className="whatsapp-order-button" onClick={handleOrderButtonClick}>Order Now via WhatsApp</button>
        </div>
      </div>
      <div className="small-image-container">
        <img src={food.image} alt={food.name} className="small-image" />
      </div>
    </Layout>
  );
};

export default FoodDetails;
