import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { MenuList } from "../data/cakes";
import "../styles/CakeDetails.css"; // Import CSS file for styling


const CakeDetails = () => {
  const { cakeId } = useParams();
  const [isZoomed, setIsZoomed] = useState(false); // State to track image zoom
  const cake = MenuList.find((cake) => cake.id === parseInt(cakeId));

  if (!cake) {
    return <div>Cake not found</div>;
  }

  const toggleZoom = () => {
    setIsZoomed(!isZoomed);
  };

  return (
    <div className="cake-details-container">
      <div className="cake-image-container">
        <img
          src={cake.image}
          alt={cake.name}
          className={isZoomed ? "zoomed-image" : "normal-image"}
          onMouseEnter={toggleZoom}
          onMouseLeave={toggleZoom}
        />
      </div>
      <div className="cake-details">
        <h2>{cake.name}</h2>
        <p>{cake.description}</p>
        <p>Price: {cake.price}</p>
      </div>
    </div>
  );
};

export default CakeDetails;
